server <- function(input, output, session) {
  rv <- reactiveValues(data = NULL, unique_obs = NULL, unique_ids = NULL, initialized = FALSE)
  
  read_data_file <- function(filepath, ext, delimiter = ",") {
    switch(ext,
           "csv" = read.csv(filepath),
           "xlsx" = read_excel(filepath),
           "xls" = read_excel(filepath),
           "txt" = read_delim(filepath, delim = delimiter),
           stop("Unsupported file type")
    )
  }
  
  observeEvent(input$file, {
    req(input$file)
    ext <- tools::file_ext(input$file$name)
    
    tryCatch({
      if (ext == "txt") {
        rv$data <- read_data_file(input$file$datapath, ext, input$delimiter)
      } else {
        rv$data <- read_data_file(input$file$datapath, ext)
      }
      
      if (inherits(rv$data, "tbl_df")) rv$data <- as.data.frame(rv$data)
      
      updateSelectInput(session, "observation_col", 
                        choices = names(rv$data),
                        selected = character(0))
      
      updateSelectInput(session, "existing_id_col",
                        choices = names(rv$data),
                        selected = character(0))
      
      rv$initialized <- FALSE
    }, error = function(e) {
      showNotification(paste("Error reading file:", e$message), type = "error")
    })
  })
  
  observeEvent(input$assignment_type, {
    if (input$assignment_type == "id") {
      updateTabsetPanel(session, "main_tabs", selected = "id_tab")
    } else if (input$assignment_type == "phase") {
      updateTabsetPanel(session, "main_tabs", selected = "phase_tab")
    } else if (input$assignment_type == "group") {
      updateTabsetPanel(session, "main_tabs", selected = "group_tab")
    } else {
      updateTabsetPanel(session, "main_tabs", selected = "id_tab")
    }
  })
  
  observeEvent(input$init_assign, {
    req(rv$data, input$observation_col)
    
    if (input$assignment_type %in% c("id", "id_phase", "id_group", "all") && !"ID" %in% names(rv$data)) {
      rv$data$ID <- NA_character_
    }
    if (input$assignment_type %in% c("phase", "id_phase", "phase_group", "all") && !"Treatment_Phase" %in% names(rv$data)) {
      rv$data$Treatment_Phase <- NA_character_
    }
    if (input$assignment_type %in% c("group", "id_group", "phase_group", "all") && !"Group" %in% names(rv$data)) {
      rv$data$Group <- NA_character_
    }
    
    rv$unique_obs <- unique(rv$data[[input$observation_col]])
    
    if (input$assignment_type %in% c("group", "id_group", "phase_group", "all")) {
      if (input$id_source == "existing" && !is.null(input$existing_id_col) && input$existing_id_col != "") {
        rv$unique_ids <- unique(rv$data[[input$existing_id_col]])
      } else {
        if ("ID" %in% names(rv$data)) {
          rv$unique_ids <- unique(na.omit(rv$data$ID))
        }
      }
    } else {
      if ("ID" %in% names(rv$data)) {
        rv$unique_ids <- unique(na.omit(rv$data$ID))
      }
    }
    
    rv$initialized <- TRUE
  })
  
  output$id_assignment_ui <- renderUI({
    req(rv$initialized, rv$unique_obs, input$max_id, 
        input$assignment_type %in% c("id", "id_phase", "id_group", "all"))
    
    valid_ids <- as.character(seq(1, input$max_id))
    
    lapply(seq_along(rv$unique_obs), function(i) {
      current_id <- unique(rv$data$ID[rv$data[[input$observation_col]] == rv$unique_obs[i]])
      current_id <- if (length(current_id) == 1) current_id else "Unassigned"
      
      fluidRow(
        column(6, p(strong(paste("Observation:", rv$unique_obs[i])))),
        column(6, selectInput(
          paste0("id_", i), NULL,
          choices = c("Unassigned", valid_ids),
          selected = current_id)
        ))
    })
  })
  
  output$phase_assignment_ui <- renderUI({
    req(rv$initialized, rv$unique_obs, input$possible_phases,
        input$assignment_type %in% c("phase", "id_phase", "phase_group", "all"))
    
    possible_phases <- trimws(unlist(strsplit(input$possible_phases, ",")))
    
    lapply(seq_along(rv$unique_obs), function(i) {
      current_phase <- unique(rv$data$Treatment_Phase[rv$data[[input$observation_col]] == rv$unique_obs[i]])
      current_phase <- if (length(current_phase) == 1) current_phase else "Unassigned"
      
      fluidRow(
        column(6, p(strong(paste("Observation:", rv$unique_obs[i])))),
        column(6, selectInput(
          paste0("phase_", i), NULL,
          choices = c("Unassigned", possible_phases),
          selected = current_phase)
        ))
    })
  })
  
  output$group_assignment_ui <- renderUI({
    req(rv$initialized, rv$unique_ids, input$predefined_groups,
        input$assignment_type %in% c("group", "id_group", "phase_group", "all"))
    
    predefined_groups <- trimws(unlist(strsplit(input$predefined_groups, ",")))
    
    lapply(seq_along(rv$unique_ids), function(i) {
      current_group <- if (input$id_source == "existing") {
        unique(rv$data$Group[rv$data[[input$existing_id_col]] == rv$unique_ids[i]])
      } else {
        unique(rv$data$Group[rv$data$ID == rv$unique_ids[i]])
      }
      current_group <- if (length(current_group) == 1) current_group else "Unassigned"
      
      fluidRow(
        column(6, p(strong(paste("ID:", rv$unique_ids[i])))),
        column(6, selectInput(
          paste0("group_", i), NULL,
          choices = c("Unassigned", predefined_groups),
          selected = current_group)
        ))
    })
  })
  
  observe({
    req(rv$initialized, rv$unique_obs, input$max_id,
        input$assignment_type %in% c("id", "id_phase", "id_group", "all"))
    
    lapply(seq_along(rv$unique_obs), function(i) {
      observeEvent(input[[paste0("id_", i)]], {
        id <- input[[paste0("id_", i)]]
        rv$data$ID[rv$data[[input$observation_col]] == rv$unique_obs[i]] <- 
          if (id == "Unassigned") NA else id
        rv$unique_ids <- unique(na.omit(rv$data$ID))
      })
    })
  })
  
  observe({
    req(rv$initialized, rv$unique_obs, input$possible_phases,
        input$assignment_type %in% c("phase", "id_phase", "phase_group", "all"))
    
    lapply(seq_along(rv$unique_obs), function(i) {
      observeEvent(input[[paste0("phase_", i)]], {
        phase <- input[[paste0("phase_", i)]]
        rv$data$Treatment_Phase[rv$data[[input$observation_col]] == rv$unique_obs[i]] <- 
          if (phase == "Unassigned") NA else phase
      })
    })
  })
  
  observe({
    req(rv$initialized, rv$unique_ids, input$predefined_groups,
        input$assignment_type %in% c("group", "id_group", "phase_group", "all"))
    
    lapply(seq_along(rv$unique_ids), function(i) {
      observeEvent(input[[paste0("group_", i)]], {
        group <- input[[paste0("group_", i)]]
        if (input$id_source == "existing" && !is.null(input$existing_id_col) && input$existing_id_col != "") {
          rv$data$Group[rv$data[[input$existing_id_col]] == rv$unique_ids[i]] <- 
            if (group == "Unassigned") NA else group
        } else {
          rv$data$Group[rv$data$ID == rv$unique_ids[i]] <- 
            if (group == "Unassigned") NA else group
        }
      })
    })
  })
  
  output$id_summary_table <- renderTable({
    req(rv$initialized, rv$data, input$max_id,
        input$assignment_type %in% c("id", "id_phase", "id_group", "all"))
    
    valid_ids <- as.character(seq(1, input$max_id))
    
    data.frame(
      ID = c(valid_ids, "Unassigned"),
      Count = c(
        sapply(valid_ids, function(id) sum(rv$data$ID == id, na.rm = TRUE)),
        sum(is.na(rv$data$ID))
      )
    )
  })
  
  output$phase_summary_table <- renderTable({
    req(rv$initialized, rv$data, input$possible_phases,
        input$assignment_type %in% c("phase", "id_phase", "phase_group", "all"))
    
    possible_phases <- trimws(unlist(strsplit(input$possible_phases, ",")))
    
    data.frame(
      Phase = c(possible_phases, "Unassigned"),
      Count = c(
        sapply(possible_phases, function(p) sum(rv$data$Treatment_Phase == p, na.rm = TRUE)),
        sum(is.na(rv$data$Treatment_Phase))
      )
    )
  })
  
  output$group_summary_table <- renderTable({
    req(rv$initialized, rv$data, input$predefined_groups,
        input$assignment_type %in% c("group", "id_group", "phase_group", "all"))
    
    predefined_groups <- trimws(unlist(strsplit(input$predefined_groups, ",")))
    
    data.frame(
      Group = c(predefined_groups, "Unassigned"),
      Count = c(
        sapply(predefined_groups, function(g) sum(rv$data$Group == g, na.rm = TRUE)),
        sum(is.na(rv$data$Group))
      )
    )
  })
  
  # output$download <- downloadHandler(
  #   filename = function() {
  #     paste0("assigned_data_", Sys.Date(), ".", 
  #            ifelse(input$download_format == "xlsx", "xlsx", "csv"))
  #   },
  #   content = function(file) {
  #     if (input$download_format == "xlsx") {
  #       write_xlsx(rv$data, file)
  #     } else {
  #       write.csv(rv$data, file, row.names = FALSE)
  #     }
  #   }
  # )
  output$download <- downloadHandler(
    filename = function() {
      paste0("assigned_data_", Sys.Date(), ".", 
             ifelse(input$download_format == "xlsx", "xlsx", "csv"))
    },
    content = function(file) {
      # Para entornos Shiny normales
      if (!isTRUE(getOption("shiny.testmode"))) {
        if (input$download_format == "xlsx") {
          write_xlsx(rv$data, file)
        } else {
          write.csv(rv$data, file, row.names = FALSE)
        }
      } else {
        # Para Shinylive (ejecución en el navegador)
        if (input$download_format == "xlsx") {
          # Convertir a CSV como solución alternativa (write_xlsx no funciona en Shinylive)
          csv_data <- write.csv(rv$data, row.names = FALSE)
          session$sendCustomMessage(
            type = "shinyliveDownload",
            message = list(
              filename = sub("\\.xlsx$", ".csv", basename(file)),
              content = csv_data
            )
          )
        } else {
          csv_data <- write.csv(rv$data, row.names = FALSE)
          session$sendCustomMessage(
            type = "shinyliveDownload",
            message = list(
              filename = basename(file),
              content = csv_data
            )
          )
        }
      }
    }
  )
  outputOptions(output, "download", suspendWhenHidden = FALSE)
}

shinyApp(ui = ui, server = server)
